package assignments;

public class Even_numbers_for_loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 0; i <= 100; i++) { //for loop for looping
			if (i % 2 == 0) {	//if statement using for conditional statement 
				System.out.println("Even numbers: " + i); //print even numbers
			}

		}
	}

}
